  --popover-foreground: hsl(200, 6.6667%, 91.1765%);
  --primary: hsl(142, 71%, 45%);
  --primary-foreground: hsl(0, 0%, 100%);
  --secondary: hsl(222, 77%, 33%);
  --secondary-foreground: hsl(0, 0%, 100%);
  --accent: hsl(164, 71%, 41%);
  --accent-foreground: hsl(0, 0%, 100%);
  --muted: hsl(0, 0%, 9.4118%);
  --muted-foreground: hsl(210, 3.3898%, 46.2745%);
  --destructive: hsl(356.3033, 90.5579%, 54.3137%);
  --destructive-foreground: hsl(0, 0%, 100%);
  --border: hsl(210, 5.2632%, 14.9020%);
  --input: hsl(207.6923, 27.6596%, 18.4314%);
  --ring: hsl(142, 71%, 45%);
}

@layer base {
  * {
    @apply border-border;
  }

  body {
    @apply font-sans antialiased bg-background text-foreground;
    font-family: 'Inter', system-ui, sans-serif;
  }
}

@layer utilities {
  .avrimile-primary {
    color: var(--avrimile-primary);
  }

  .bg-avrimile-primary {
    background-color: var(--avrimile-primary);
  }

  .avrimile-secondary {
    color: var(--avrimile-secondary);
  }

  .bg-avrimile-secondary {
    background-color: var(--avrimile-secondary);
  }

  .avrimile-accent {
    color: var(--avrimile-accent);
  }

  .bg-avrimile-accent {
    background-color: var(--avrimile-accent);
  }

  .avrimile-muted {
    color: var(--avrimile-muted);
  }

  .bg-avrimile-muted {
    background-color: var(--avrimile-muted);
  }

  /* Section animations */
  .animate-in {
    animation: fadeInUp 0.6s ease-out forwards;
  }

  .animate-out {
    animation: fadeOutDown 0.3s ease-in forwards;
  }
}

@keyframes fadeInUp {
  from {
    opacity: 0;
    transform: translateY(30px);
  }
  to {
    opacity: 1;
    transform: translateY(0);
  }
}

@keyframes fadeOutDown {
  from {
    opacity: 1;
    transform: translateY(0);
  }
  to {
    opacity: 0.3;
    transform: translateY(-15px);
  }
}

/* Icon animations */
@keyframes bounce {
  0%, 20%, 53%, 80%, 100% {
    transform: translate3d(0, 0, 0);
  }
  40%, 43% {
    transform: translate3d(0, -8px, 0);
  }
  70% {
    transform: translate3d(0, -4px, 0);
  }
  90% {
    transform: translate3d(0, -2px, 0);
  }
}

@keyframes pulse {
  0% {
    transform: scale(1);
  }
  50% {
    transform: scale(1.05);
  }
  100% {
    transform: scale(1);
  }
}

@keyframes rotate {
  from {
    transform: rotate(0deg);
  }
  to {
    transform: rotate(360deg);
  }
}

.animate-bounce-slow {
  animation: bounce 2s infinite;
}

.animate-pulse-slow {
  animation: pulse 3s ease-in-out infinite;
}

.animate-rotate-slow {
  animation: rotate 4s linear infinite;
}

